mapper.select:
SELECT member_no,member_id,member_email 
FROM member 
WHERE 

	member_name = #{member_name} and member_hp = #{member_hp}


	member_id = #{member_id} and member_email = #{member_email}

mapper.update:
UPDATE member 
SET member_password = #{member_password}
WHERE member_no = #{member_no}

mapper.select:
SELECT * FROM(SELECT ROWNUM AS num, A.* FROM ( SELECT m.MEMBER_IMAGE, m.MEMBER_NICKNAME, r.REVIEW_CONTENT, r.REVIEW_SCORE, r.REVIEW_REGDATE, p.PRODUCT_NAME, p.PRODUCT_NO FROM deal d JOIN review r ON r.deal_no = d.deal_no JOIN member m ON m.member_no = d.seller_no JOIN product p ON p.product_no = d.product_no WHERE d.customer_no = #{member_no}
AND r.review_score = 1
AND r.review_score = 2
AND r.review_score = 3
AND r.review_score = 4
AND r.review_score = 5
ORDER BY r.REVIEW_REGDATE DESC
ORDER BY r.REVIEW_REGDATE
ORDER BY r.REVIEW_REGDATE DESC
) A ) WHERE num BETWEEN #{startPage} AND #{endPage}
