				 , member_no
				 , report_member_no
				 , report_category1_no
				 , report_category2_no
				 , report_content
				 , report_image
				 , report_info)
	values(#{report_no}
		 , #{member_no}
		 , #{report_member_no}
		 , #{report_category1_no}
		 , #{report_category2_no}
		 , #{report_content}
		 , #{report_image}
		 , #{report_info})
